function GetTodayDate() {
    var tdate = new Date();
    var dd = tdate.getDate(); //yields day
    var MM = tdate.getMonth(); //yields month
    var yyyy = tdate.getFullYear(); //yields year
   // var xxx = dd + "-" +( MM+1) + "-" + yyyy;

    var xxx = yyyy + "/" + ( MM+1) + "/" + dd;

    return xxx;
}

