(function ($) {
    // validate signup form on keyup and submit
    $(".co-form").validate({
        rules: {
            co_name: "required",
            co_mobile: {
                required: true,
                maxlength: 12,
                minlength: 9,
                digits: true
            },
            co_mobile_inv: {
                maxlength: 12,
                minlength: 9,
                digits: true
            },
            co_conditioners_number: {
              required: true,
              min: 1
            },
            co_ctype: "required",
            co_service: "required",
            co_date: "required",
            co_time: "required",
            co_neighbourhood_name: "required"
        },
        messages: {
             co_name: coparams.errors.invalid_name,
             co_mobile: coparams.errors.invalid_mobile,
             co_mobile_inv: coparams.errors.invalid_mobile_inv,
            "co_conditioners_type[]": coparams.errors.invalid_ctype,
            "co_service[]": coparams.errors.invalid_service,
             co_date: coparams.errors.invalid_date,
             co_time: coparams.errors.invalid_time,
             co_neighbourhood_name: coparams.errors.invalid_neighbourhood_name,
             co_conditioners_number: coparams.errors.co_conditioners_number_invalid
        },
        errorPlacement: function(error, element) {
            error.insertBefore(element);
        },
        submitHandler: function(form) {

            //alert($(form).find('#co_name').val());
            var wrapper = $(form).closest(".co-order-form");
            var step1 = $(form).closest(".step1");
            var step2 = wrapper.find(".step2");
            var orderStatusWrapper = step1.find(".co_order_status");
            orderStatusWrapper.hide();
            orderStatusWrapper.html("");
            orderStatusWrapper.removeClass('co-errors');
            orderStatusWrapper.removeClass('co-success');
            orderStatusWrapper.addClass('co-normal');

            var co_name = $(form).find("#co_name");
            var co_mobile = $(form).find("#co_mobile");
            var co_mobile_inv = $(form).find("#co_mobile_inv");
            var co_ctype = $(form).find(".co_conditioners_type");
            var co_service = $(form).find(".co_service");
            var co_date = $(form).find("#co_date");
            var co_time = $(form).find("#co_time");
            var co_neighbourhood_name = $(form).find("#co_neighbourhood_name");
            var co_conditioners_number = $(form).find("#co_conditioners_number");
            var cptxdis = $(form).find("#cptxdis");
            var gcaptcha = $(form).find(".g-recaptcha");

            var args = [], arr = [], k = 0;
            args["action"] = "neworderpost";
            args["ordernonce"] = coparams.ajax_nonce;

            if(co_name.length > 0){
                args["co_name"] = co_name.val();
            }else{
                args["co_name"] = "";
            }
            if(co_mobile.length > 0){
                args["co_mobile"] = co_mobile.val();
            }else{
                args["co_mobile"] = "";
            }
            if(co_mobile_inv.length > 0){
                args["co_mobile_inv"] = co_mobile_inv.val();
            }else{
                args["co_mobile_inv"] = "";
            }
            if(co_ctype.length > 0){
                args["co_ctype"] = [];
                arr = [];
                k = 0;
                co_ctype.each(function () {
                    if($(this).is(":checked")){
                        arr[k++] = $(this).val();
                    }
                });
                args["co_ctype"] = arr;
                console.log("SELECTED Types: " + arr);
            }else{
                args["co_ctype"] = "";
            }
            if(co_service.length > 0){
                args["co_service"] = [];
                arr = [];
                k = 0;
                co_service.each(function () {
                    if($(this).is(":checked")){
                        arr[k++] = $(this).val();
                    }
                });
                console.log("SELECTED SERVICES: " + arr);
                args["co_service"] = arr;
            }else{
                args["co_service"] = "";
            }
            if(co_time.length > 0){
                args["co_time"] = co_time.val();
            }else{
                args["co_time"] = "";
            }

            if(co_date.length > 0){
                args["co_date"] = co_date.val();
            }else{
                args["co_date"] = "";
            }

            if(co_neighbourhood_name.length > 0){
                args["co_neighbourhood_name"] = co_neighbourhood_name.val();
            }else{
                args["co_neighbourhood_name"] = "";
            }
            if(gcaptcha.length > 0){
                args["g-recaptcha-response"] = grecaptcha.getResponse();
            }else{
                args["g-recaptcha-response"] = "";
            }

            if(cptxdis.length > 0){
                args["cptxdis"] = cptxdis.val();
            }else{
                args["cptxdis"] = "";
            }

            if(co_conditioners_number.length > 0){
                args['co_conditioners_number'] = co_conditioners_number.val();
            }else {
                args['co_conditioners_number'] = 0;
            }

            var spinner = null;
            var spinner_div = document.getElementById('spinner');

            if(spinner == null) {
                spinner = new Spinner(opts).spin(spinner_div);
            } else {
                spinner.spin(spinner_div);
            }


            //alert("sending request!");
            $.ajax({
                url: coparams.ajax_url,
                dataType:"json",
                type: 'POST',
                data: {
                    action : "neworderpost",
                    ordernonce : coparams.ajax_nonce,
                    co_name : args["co_name"],
                    co_mobile : args["co_mobile"],
                    co_mobile_inv : args["co_mobile_inv"],
                    "co_conditioners_type[]" : args["co_ctype"],
                    "co_service[]" : args["co_service"],
                    co_time : args["co_time"],
                    co_date : args["co_date"],
                    co_neighbourhood_name : args["co_neighbourhood_name"],
                    co_conditioners_number : args["co_conditioners_number"],
                    "g-recaptcha-response" : args["g-recaptcha-response"],
                    cptxdis : args["cptxdis"]
                },
                success: function(response) {
                    console.log(response);

                    if(response.status == 'success')
                    {
                        if(response.proceed_verify && response.proceed_verify == "yes") {  // If we need to proceed to sms verification

                            step1.remove();
                            step2.html(response.verification_form);
                            var order_id = response.new_order_id;
                            if(Number(order_id) > 0){
                                step2.fadeIn();
                                step2.find("#co_order_id").val(order_id);
                            }
                            $('html, body').animate({scrollTop : 0},800);
                        }else if( response.proceed_verify && response.proceed_verify == "no" ){ // If we dont need to proceed to sms verfication
                            step2.remove();
                            $(form).remove();
                            orderStatusWrapper.addClass('co-success');
                            orderStatusWrapper.html(response.htmlmessages);
                            orderStatusWrapper.fadeIn('slow');
                            $('html, body').animate({scrollTop : 0},800);
                        }else{
                            alert('exception: uncaught error occoured');
                        }

                    } else if(response.status == 'done'){

                        $(form).remove();
                        orderStatusWrapper.addClass('co-success');
                        orderStatusWrapper.html(response.htmlmessages);
                        orderStatusWrapper.fadeIn();
                        $('html, body').animate({scrollTop : 0},800);

                    } else if(response.status == 'error')
                    {
                        orderStatusWrapper.addClass('co-errors');
                        orderStatusWrapper.html("<p>" + coparams.errors.errors_found + "</p>" + response.htmlmessages);
                        orderStatusWrapper.fadeIn('slow');
                    }
                    if( grecaptcha ){
                        grecaptcha.reset();
                    }
                    
                    spinner.stop(spinner_div);
                }
            });


            spinner.stop(spinner_div);

            return false;
        }
    });
})(jQuery);
///===============================///
///    ORDER PLACE SCRIPT END    ///
///==============================///










