///================================================///
///    HIDE DATES BEFORE CURRENT TIME OF TODAYS   ///
///===============================================///

(function($){

    function checkOrDisableTimeForThisDateOption( optionSelected )
    {
        var co_time_options = $("#co_time option");
        var today    = Date.today();
        var selected = Date.parse(optionSelected.val());
        if(Date.equals(today, selected)){
            if(co_time_options.length > 0) {
                co_time_options.each(function() {
                    var currentTime        = new Date().toString("h:mmtt").toLocaleLowerCase();
                    var currentOptionTime  = $(this).attr("value");
                    var currentDate = Date.today().at(currentTime);
                    var currentOptionDate = Date.today().at(currentOptionTime);


                    //need to be 30 minutes
                    //1800000 is 30 minutes in ms
                    var difference =  currentDate.getTime()-currentOptionDate.getTime();
                    // && difference > 1800000*2 
                    
                    // The order time must be minimum after 3 hours
                    if( (currentDate.getTime()+3*3600000 > currentOptionDate.getTime())){
                        console.log(difference + " > Â *2");
                        //console.log(currentDate.getTime() - currentOptionDate.getTime());
                        $(this).prop("disabled", true);
                        $(this).prop("selected", false);
                    }else{
                        $(this).prop("disabled", false);
                    }
                });
            }
        }
    }

    $(window).load(function(){
        var optionSelected = $("#co_date option:selected");
	if( optionSelected.length ) {
        	checkOrDisableTimeForThisDateOption(optionSelected);
	}
    });

    var co_date = $("#co_date");
    var co_time = $("#co_time");
    var co_time_options = co_time.find("option");
    co_date.on("change", function(){
        var optionSelected = $("option:selected", this);
        var allOptions     = $("option", this);
        co_time_options.each(function(i,self){
            $(self).prop("disabled", false);
            $(this).removeAttr("disabled");
        });
        checkOrDisableTimeForThisDateOption(optionSelected);
    });

})(jQuery);

///================================================///
///    HIDE DATES BEFORE CURRENT TIME OF TODAYS  END  ///
///===============================================///









